mapper.insert:
/* bot.xml insertBotWordHis */
INSERT INTO TBOT_WORD_HIS
	 (REQ
	 ,RES
	 ,ROOM_NAME
	 ,USER_NAME
	 ,INSERT_DATE
	 ,ISSUE_YN)
VALUES
	 (#{req, jdbcType=VARCHAR}
	 ,#{res, jdbcType=VARCHAR}
	 ,#{roomName, jdbcType=VARCHAR}
	 ,#{userName, jdbcType=VARCHAR}
	 ,sysdate
	 ,#{issue_yn, jdbcType=VARCHAR})

mapper.select:
/* bot.xml selectMarketCondition */ SELECT XX.DT , MIN(XX.GOLD) || '~' || MAX(XX.GOLD) AS SISE , MAX(XX.INSERT_DATE) FROM (SELECT X.DT , TO_NUMBER(SUBSTR(X.MSG, INSTR(X.MSG, '-') + 2, INSTR(SUBSTR(X.MSG, INSTR(X.MSG, '-') + 2), 'G') - 1)) AS GOLD , X.INSERT_DATE FROM (
SELECT AA.MON || '월 ' || AA.WEEK_OF_MONTH || '주차' AS DT , SUBSTR(TO_CHAR(B.RES), INSTR(TO_CHAR(B.RES), #{param1, jdbcType=VARCHAR}), INSTR(SUBSTR(TO_CHAR(B.RES), INSTR(TO_CHAR(B.RES), #{param1, jdbcType=VARCHAR}), 30), '♬')) AS MSG , B.INSERT_DATE FROM TDATE AA, TBOT_WORD_HIS B WHERE RES IS NOT NULL AND (B.REQ LIKE '/ㄱㅁㅈ%' OR B.REQ LIKE '/경매장%') AND B.RES LIKE '%' || #{param1, jdbcType=VARCHAR} || '%' AND B.INSERT_DATE BETWEEN AA.WEEK_START AND AA.WEEK_END AND B.INSERT_DATE > SYSDATE - 60 ) X
) XX WHERE GOLD > (
SELECT MAX(TO_NUMBER(SUBSTR(X2.MSG, INSTR(X2.MSG, '-') + 2, INSTR(SUBSTR(X2.MSG, INSTR(X2.MSG, '-') + 2), 'G') - 1))) * 0.5 AS GOLD FROM (
SELECT AA.MON || '월 ' || AA.WEEK_OF_MONTH || '주차' AS DT ,SUBSTR(TO_CHAR(B.RES), INSTR(TO_CHAR(B.RES), #{param1, jdbcType=VARCHAR}), INSTR(SUBSTR(TO_CHAR(B.RES), INSTR(TO_CHAR(B.RES), #{param1, jdbcType=VARCHAR}), 30), '♬')) AS MSG FROM TDATE AA , TBOT_WORD_HIS B WHERE RES IS NOT NULL AND (B.REQ LIKE '/ㄱㅁㅈ%' OR B.REQ LIKE '/경매장%') AND B.RES LIKE '%' || #{param1, jdbcType=VARCHAR} || '%' AND B.INSERT_DATE BETWEEN AA.WEEK_START AND AA.WEEK_END AND B.INSERT_DATE > SYSDATE - 60 ) X2 WHERE X2.DT = XX.DT GROUP BY DT) GROUP BY DT ORDER BY 3 DESC

