mapper.select:
SELECT MAX(INSERT_DATE)
  FROM TBOT_POINT_NEW_BATTLE_LOG
 WHERE USER_NAME = #{userName}
   AND ROOM_NAME = #{roomName}

mapper.update:
UPDATE TBOT_POINT_NEW_USER
   SET LV       = #{newLv},
       EXP_CUR  = #{newExpCur},
       EXP_NEXT = #{newExpNext},
       HP_CUR   = #{newHpCur},
       HP_MAX   = #{newHpMax},
       ATK_MIN  = #{newAtkMin},
       ATK_MAX  = #{newAtkMax}
 WHERE USER_NAME = #{userName}
   AND ROOM_NAME = #{roomName}

UPDATE TBOT_POINT_NEW_BATTLE_LOG
   SET NOW_YN = '0'
 WHERE USER_NAME = #{userName}
   AND ROOM_NAME = #{roomName}
   AND NOW_YN = '1'

UPDATE TBOT_POINT_NEW_USER
   SET HP_CUR = #{newHpCur}
 WHERE USER_NAME = #{userName}
   AND ROOM_NAME = #{roomName}

UPDATE TBOT_POINT_NEW_USER
   SET TARGET_MON = #{newMonNo}
 WHERE USER_NAME = #{userName}
   AND ROOM_NAME = #{roomName}
